import React, { useState } from 'react';

const likePost = async (post) => {
  const response = await fetch(`http://localhost:3001/users/${currentUser}/posts/${post.post_title}/like`, {
    method: 'PATCH',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ likes: post.likes + 1 }),
  });
}

const Posts = ({username}) => {

  const [posts, setPosts] = useState([]);
  const [currentUser, setCurrentUser] = useState({});

  const fetchPosts = async () => {
    const response = await fetch(`http://localhost:3001/users/${username}/posts`);
    const data = await response.json();
    setPosts(data);
    setCurrentUser(username);
  }

  return (
    <div>
      <h2>Posts</h2>
      <button onClick={fetchPosts}>Fetch Posts</button>
      <ul>
        {posts.map((post, index) => (
          <li key={index}>
            <h3>{post.post_title}</h3>
            <img src={post.post_image_url} alt={post.post_title} />
            <p>{post.meal_type}</p>
            <div>
            <p>{post.likes} likes</p>
            <button onclick={likePost(post, currentUser)}>Like</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Posts;
